create procedure addClassAssociation(in associationId int,in envName text, in headAsset text, in headType text, in headNav int, in headMult text,in headRole text, in tailRole text, in tailMult text, in tailNav int, in tailType text, in tailAsset text, in assocRationale text)
begin
  declare environmentId int;
  declare headAssetId int;
  declare headTypeId int;
  declare headMultId int;
  declare tailMultId int;
  declare tailTypeId int;
  declare tailAssetId int;

  select id into environmentId from environment where name = envName limit 1;
  select id into headAssetId from asset where name = headAsset limit 1;
  select id into headTypeId from association_type where name = headType limit 1;
  select id into headMultId from multiplicity_type where name = headMult limit 1;
  select id into tailMultId from multiplicity_type where name = tailMult limit 1;
  select id into tailTypeId from association_type where name = tailType limit 1;
  select id into tailAssetId from asset where name = tailAsset limit 1;

  call addAssetDependents(headAssetId,tailAssetId,environmentId);

  insert into classassociation(id,environment_id,head_id,head_association_type_id,head_navigation,head_multiplicity_id,head_role_name,tail_role_name,tail_multiplicity_id,tail_navigation,tail_association_type_id,tail_id,rationale) values(associationId,environmentId,headAssetId,headTypeId,headNav,headMultId,headRole,tailRole,tailMultId,tailNav,tailTypeId,tailAssetId,assocRationale);
end
//

create procedure updateClassAssociation(in associationId int,in envName text, in headAsset text, in headType text, in headNav int, in headMult text,in headRole text, in tailRole text, in tailMult text, in tailNav int, in tailType text, in tailAsset text, in assocRationale text)
begin
  declare environmentId int;
  declare headAssetId int;
  declare headTypeId int;
  declare headMultId int;
  declare tailMultId int;
  declare tailTypeId int;
  declare tailAssetId int;

  select id into environmentId from environment where name = envName limit 1;
  select id into headAssetId from asset where name = headAsset limit 1;
  select id into headTypeId from association_type where name = headType limit 1;
  select id into headMultId from multiplicity_type where name = headMult limit 1;
  select id into tailMultId from multiplicity_type where name = tailMult limit 1;
  select id into tailTypeId from association_type where name = tailType limit 1;
  select id into tailAssetId from asset where name = tailAsset limit 1;

  call addAssetDependents(headAssetId,tailAssetId,environmentId);

  update classassociation set environment_id = environmentId, head_id = headAssetId, head_association_type_id = headTypeId, head_multiplicity_id = headMultId, head_role_name = headRole, tail_role_name = tailRole, tail_multiplicity_id = tailMultId, tail_association_type_id = tailTypeId, tail_id = tailAssetId, head_navigation = headNav, tail_navigation = tailNav, rationale = assocRationale where id = associationId;
end
//

create procedure delete_classassociation(in associationId int)
begin
  delete from classassociation where id = associationId;
end
//

create function classAssociationId(envName text,headName text,tailName text) 
returns int
deterministic 
begin
  declare envId int;
  declare headId int;
  declare tailId int;
  declare associationId int;

  select id into envId from environment where name = envName;
  select id into headId from asset where name = headName;
  select id into tailId from asset where name = tailName;

  
  select id into associationId from classassociation where environment_id = envId and head_id = headId and tail_id = tailId;
  return associationId;

end
