create procedure dimensionId(in dimText text, in dimTable text)
begin
  declare shortCode varchar(100);
  declare reqLabel int;
  declare objtId int;
  declare dimSql varchar(4000);

  if dimTable != 'requirement' and dimTable != 'usecase' and dimTable != 'task' and dimTable != 'user_goal'
  then
    set dimSql = concat('select id from ',dimTable,' where ');

    if dimTable = 'persona_characteristic' or dimTable = 'task_characteristic' 
    then
      set dimSql = concat(dimSql,'description');
    elseif dimTable = 'synopsis'
    then
      set dimSql = concat(dimSql,'synopsis');
    else
      set dimSql = concat(dimSql,'name');
    end if;
    set dimSql = concat(dimSql,' = \"',dimText,'\"');

    set @sql = dimSql;
    prepare stmt from @sql;
    execute stmt;
    deallocate prepare stmt;
  elseif dimTable = 'usecase'
  then
    select id into objtId from usecase where name = dimText;
    if objtId is null
    then
      select id into objtId from usecase where short_code = dimText;
    end if;  
    select objtId;
  elseif dimTable = 'task'
  then
    select id into objtId from task where name = dimText;
    if objtId is null
    then
      select id into objtId from task where short_code = dimText;
    end if;  
    select objtId;
  elseif dimTable = 'user_goal'
  then
    select id into objtId from document_reference_synopsis where synopsis = dimText;
    if objtId is null
    then
      select characteristic_id into objtId from persona_characteristic_synopsis where synopsis = dimText;
    end if;
    select objtId;
  else
    select o.id into objtId from requirement o where o.name = dimText and o.version = (select max(i.version) from requirement i where i.id = o.id);
    if objtId is null
    then
      call requirementLabelComponents(dimText,shortCode,reqLabel);
      select o.id into objtId from requirement o, asset_requirement ar, asset a where o.label = reqLabel and o.id = ar.requirement_id and ar.asset_id = a.id and a.short_code = shortCode and o.version = (select max(i.version) from requirement i where i.id = o.id);
      if objtId is null
      then
        select o.id from requirement o, environment_requirement ar, environment a where o.label = reqLabel and o.id = ar.requirement_id and ar.environment_id = a.id and a.short_code = shortCode and o.version = (select max(i.version) from requirement i where i.id = o.id);
      else
        select objtId;
      end if;
    end if;
    select objtId;
  end if;
end
//

create procedure getDimensions(in dimensionTable text, in constraintId int)
begin
  declare dimSql varchar(4000);
  declare nameCol varchar(11) default 'name';

  if dimensionTable = 'detection_mechanism'
  then
    set dimSql = 'select dm.asset_id, a.name from detection_mechanism dm, asset a where dm.asset_id = a.id order by 2';
  elseif dimensionTable = 'requirement' and constraintId = -1
  then
    set dimSql = 'select o.id,o.name from requirement o where o.version = (select max(i.version) from requirement i where i.id = o.id)';
  elseif dimensionTable = 'noncomposite_environment'
  then
    set dimSql = 'select id, name from environment where id not in (select composite_environment_id from composite_environment) order by 1';
  else
    if dimensionTable = 'persona_characteristic'
    then
      set nameCol = 'description';
    end if;

    if dimensionTable = 'process'
    then
      set dimensionTable = 'usecase';
    end if;
 
    if constraintId = -1
    then
      set dimSql = concat('select id, ',nameCol,' from ',dimensionTable,' order by 2');
    else
      set dimSql = concat('select id, ',nameCol,' from ',dimensionTable,' where id = ',constraintId,' order by 2');
    end if;
  end if;
  set @sql = dimSql;
  prepare stmt from @sql;
  execute stmt;
  deallocate prepare stmt;
end
