create procedure subGoalTree(in goalId int, in environmentId int,in caseFilter int)
begin
  declare sgId int;
  declare compositeCount int;
  declare environmentName varchar(50);
  declare done int default 0;
  declare sgCursor cursor for select subgoal_id from goalgoal_goalassociation where goal_id = goalId and environment_id = environmentId union select subgoal_id from goalobstacle_goalassociation where goal_id = goalId and environment_id = environmentId union select subgoal_id from goalrequirement_goalassociation where goal_id = goalId and environment_id = environmentId union select subgoal_id from obstacleobstacle_goalassociation where goal_id = goalId and environment_id = environmentId union select subgoal_id from obstaclegoal_goalassociation where goal_id = goalId and environment_id = environmentId union select subgoal_id from obstaclerequirement_goalassociation where goal_id = goalId and environment_id = environmentId;
  declare continue handler for not found set done = 1;

  select name into environmentName from environment where id = environmentId;
  select count(*) into compositeCount from composite_environment where composite_environment_id = environmentId;

  if compositeCount <= 0
  then
    insert into temp_goaltree (id,environment,goal_name,goal_dim,ref_type,subgoal_name,subgoal_dim,alternative_id,rationale)
