  end if;

  set done = 0;
  open sgCursor;
  sg_loop: loop
    fetch sgCursor into sgId;
    if done = 1
    then
      leave sg_loop;
    end if;
    call subGoalTree(sgId,environmentId,caseFilter);
  end loop sg_loop;
  close sgCursor;
end
