create procedure subTaskModel(in taskName text, in envName text)
begin
  declare taskId int;
  declare environmentId int;
  declare compositeCount int;
  select id into taskId from task where name = taskName limit 1;
  select id into environmentId from environment where name = envName limit 1;
  select count(*) into compositeCount from composite_environment where composite_environment_id = environmentId;

  if compositeCount <= 0
  then
    select -1 id,e.name environment,t.name goal_name,'task' goal_dim,'task_association' ref_type,p.name subgoal_name,'persona' subgoal_dim,'0' alternative_id, '' rationale from task_persona tp, environment e, task t, persona p where tp.task_id = taskId and tp.environment_id = environmentId and tp.environment_id = e.id and tp.task_id = t.id and tp.persona_id = p.id
    union
    select -1 id,e.name environment,r.name goal_name,'role' goal_dim,'rolepersona_association' ref_type,p.name subgoal_name,'persona' subgoal_dim,'0' alternative_id, '' rationale from persona_role pr, task_persona tp, environment e, environment_usecase eu, role r, persona p where pr.environment_id = environmentId and pr.environment_id = e.id and pr.persona_id = p.id and pr.role_id = r.id and pr.persona_id = tp.persona_id and tp.task_id = taskId and tp.environment_id = pr.environment_id and tp.environment_id = eu.environment_id
    union
    select -1 id,e.name environment,t.name goal_name,'task' goal_dim,'taskasset_association' ref_type,a.name subgoal_name,'asset' subgoal_dim,'0' alternative_id, '&lt;&lt;uses&gt;&gt;' rationale from task_asset ta, environment e, task t, asset a where ta.task_id = taskId and ta.environment_id = environmentId and ta.environment_id = e.id and ta.task_id = t.id and ta.asset_id = a.id
    union
    select -1 id,e.name environment,u.name goal_name,'usecase' goal_dim,'usecasetask_association' ref_type,t.name subgoal_name,'task' subgoal_dim,'0' alternative_id, '&lt;&lt;consequence&gt;&gt;' rationale from usecase_task ut, environment e, usecase u, task t, environment_usecase eu, environment_task et where eu.environment_id = environmentId and eu.environment_id = e.id and eu.environment_id = et.environment_id and ut.task_id = taskId and ut.usecase_id = eu.usecase_id and eu.usecase_id = u.id and ut.task_id = et.task_id and et.task_id = t.id
    union
    select -1 id,e.name environment,u.name goal_name,'usecase' goal_dim,'usecase_association' ref_type,r.name subgoal_name,'role' subgoal_dim,'0' alternative_id, '' rationale from usecase_task ut, environment e, usecase u, usecase_role ur, role r, environment_usecase eu, environment_task et where eu.environment_id = environmentId and eu.environment_id = e.id and eu.environment_id = et.environment_id and ut.usecase_id = eu.usecase_id and eu.usecase_id = u.id and ut.task_id = taskId and ut.task_id = et.task_id and eu.usecase_id = u.id and eu.usecase_id = ur.usecase_id and ur.role_id = r.id
    union
    select -1 id,e.name environment,mc.name goal,'misusecase' goal_dim,'misusecase_association' ref_type,a.name subgoal_name, 'attacker' subgoal_dim,'0' alternative_id, '' rationale from misusecase mc, misusecase_risk mr, risk r, threat_attacker ta,attacker a,environment e, asset_threat at, task_asset taa where mc.id = mr.misusecase_id and mr.risk_id = r.id and r.threat_id = ta.threat_id and ta.environment_id = environmentId and ta.environment_id = e.id and ta.attacker_id = a.id and r.threat_id = at.threat_id and at.environment_id = environmentId and at.asset_id = taa.asset_id and taa.task_id = taskId and at.environment_id = taa.environment_id
