create procedure nameExists(in objtName text, in dimName text)
begin
  declare objtCount int;
  declare nameVar varchar(11) default 'name';
  declare ncSql varchar(4000);

  if dimName = 'persona_characteristic' or dimName = 'task_characteristic'
  then
    set nameVar = 'description';
  end if;

  if dimName = 'requirement'
  then
    set ncSql = concat('select count(o.id) from requirement o where o.name = "',objtName,'" and o.version = (select max(i.version) from requirement i where i.id = o.id) limit 1');
  else
    set ncSql = concat('select count(id) into @objtCount from ',dimName,' where ',nameVar,' = "',objtName,'" limit 1');
  end if;
  set @sql = ncSql;
  prepare stmt from @sql;
  execute stmt;
  deallocate prepare stmt;
  set objtCount = @objtCount;

  if objtCount = 0
  then
    set ncSql = concat('select count(name) into @objtCount from object_name where name = "',objtName,'" limit 1');
    set @sql = ncSql;
    prepare stmt from @sql;
    execute stmt;
    deallocate prepare stmt;
    set objtCount = @objtCount;
  end if;
  select objtCount;
end
