  close envCursor;

  set ixtSql = concat(ixtSql,')');
  set vxtSql = concat(vxtSql,')');
  set xtSql = concat(ixtSql,' ',vxtSql);

  set @sql = xtSql;
  prepare stmt from @sql;
  execute stmt;
  deallocate prepare stmt;
end
