create function cnToGn(dimName text)
returns varchar(50)
deterministic
begin
  declare grlName varchar(50);

  if dimName = 'goal'
  then
    set grlName = 'Goal';
  elseif dimName = 'task'
  then
    set grlName = 'Task';
  else
    set grlName = 'Softgoal';
  end if;
  return grlName;
end
