create function obstacle_label(obsId int,environmentId int) 
returns varchar(255)
deterministic 
begin
  declare lblTxt varchar(255) default '';

  select label into lblTxt from obstacle_label where obstacle_id = obsId and environment_id = environmentId;
  return lblTxt;
end
