create procedure riskAnalysisModelElements(in environmentName text)
begin
  declare environmentId int;
  declare compositeCount int;
  select id into environmentId from environment where name = environmentName limit 1;
  select count(*) into compositeCount from composite_environment where composite_environment_id = environmentId;

  if compositeCount <= 0
  then
    select 'asset',a.name from asset a,environment_asset ea where ea.environment_id = environmentId and ea.asset_id = a.id 
    union
    select 'attacker',a.name from attacker a, environment_attacker ea where ea.environment_id = environmentId and ea.attacker_id = a.id
    union
    select 'countermeasure',c.name from countermeasure c, environment_countermeasure ec where ec.environment_id = environmentId and ec.countermeasure_id = c.id 
    union
    select 'obstacle',o.name from obstacle o, environment_obstacle eo where eo.environment_id = environmentId and eo.obstacle_id = o.id
    union
    select 'requirement', concat(a.short_code,'-',r.label) from requirement r, asset_requirement ar, environment_asset ea, asset a where ea.environment_id = environmentId and ea.asset_id = ar.asset_id and ea.asset_id = a.id and  ar.requirement_id = r.id and r.version = (select max(i.version) from requirement i where i.id = r.id)
    union
    select 'requirement', concat(e.short_code,'-',r.label) from requirement r, environment_requirement er, asset e where er.environment_id = environmentId and er.environment_id = e.id and  er.requirement_id = r.id and r.version = (select max(i.version) from requirement i where i.id = r.id)
    union
    select 'response',r.name from response r, environment_response er  where er.environment_id = environmentId and er.response_id = r.id
    union
    select 'risk',r.name from risk r, environment_risk er  where er.environment_id = environmentId and er.id = r.id
    union
    select 'role',r.name from role r, environment_role er  where er.environment_id = environmentId and er.role_id = r.id
    union
    select 'task',t.name from task t, environment_task et  where et.environment_id = environmentId and et.task_id = t.id
    union
    select 'threat',t.name from threat t, environment_threat et  where et.environment_id = environmentId and et.threat_id = t.id
    union
    select 'vulnerability',v.name from vulnerability v, environment_vulnerability ev  where ev.environment_id = environmentId and ev.vulnerability_id = v.id order by 2;
  end if;
end
//

create procedure environmentDependents(in envId int)
begin
  declare done int default 0;
  declare dimId int;
  declare dimName varchar(200);
  declare assetCursor cursor for select distinct d.id, d.name from environment_asset ed, asset d where ed.environment_id = envId and ed.asset_id = d.id;  
  declare attackerCursor cursor for select distinct d.id, d.name from environment_attacker ed, attacker d where ed.environment_id = envId and ed.attacker_id = d.id;  
  declare cmCursor cursor for select distinct d.id, d.name from environment_countermeasure ed, countermeasure d where ed.environment_id = envId and ed.countermeasure_id = d.id;  
  declare goalCursor cursor for select distinct d.id, d.name from environment_goal ed, goal d where ed.environment_id = envId and ed.goal_id = d.id;  
  declare mcCursor cursor for select distinct d.id, d.name from environment_misusecase ed, misusecase d where ed.environment_id = envId and ed.misusecase_id = d.id;  
  declare obsCursor cursor for select distinct d.id, d.name from environment_obstacle ed, obstacle d where ed.environment_id = envId and ed.obstacle_id = d.id;  
  declare personaCursor cursor for select distinct d.id, d.name from environment_persona ed, persona d where ed.environment_id = envId and ed.persona_id = d.id;  
  declare reqCursor cursor for select distinct d.id, d.name from environment_requirement ed, requirement d where ed.environment_id = envId and ed.requirement_id = d.id;  
  declare respCursor cursor for select distinct d.id, d.name from environment_response ed, response d where ed.environment_id = envId and ed.response_id = d.id;  
  declare riskCursor cursor for select distinct d.id, d.name from environment_risk ed, risk d where ed.environment_id = envId and ed.id = d.id;  
  declare roleCursor cursor for select distinct d.id, d.name from environment_role ed, role d where ed.environment_id = envId and ed.role_id = d.id;  
  declare taskCursor cursor for select distinct d.id, d.name from environment_task ed, task d where ed.environment_id = envId and ed.task_id = d.id;  
  declare thrCursor cursor for select distinct d.id, d.name from environment_threat ed, threat d where ed.environment_id = envId and ed.threat_id = d.id;  
  declare ucCursor cursor for select distinct d.id, d.name from environment_usecase ed, usecase d where ed.environment_id = envId and ed.usecase_id = d.id; 
  declare vulCursor cursor for select distinct d.id, d.name from environment_vulnerability ed, vulnerability d where ed.environment_id = envId and ed.vulnerability_id = d.id;  
  declare continue handler for not found set done = 1;

  open assetCursor;
  asset_loop: loop
    fetch assetCursor into dimId,dimName;
    if done = 1
    then
      leave asset_loop;
    end if;
    insert into temp_asset values(dimId,dimName);
  end loop asset_loop;
  close assetCursor;
  set done = 0;

  open attackerCursor;
  attacker_loop: loop
    fetch attackerCursor into dimId,dimName;
    if done = 1
    then
      leave attacker_loop;
    end if;
    insert into temp_attacker values(dimId,dimName);
  end loop attacker_loop;
  close attackerCursor;
  set done = 0;

  open cmCursor;
  cm_loop: loop
    fetch cmCursor into dimId,dimName;
    if done = 1
    then
      leave cm_loop;
    end if;
    insert into temp_countermeasure values(dimId,dimName);
  end loop cm_loop;
  close cmCursor;
  set done = 0;

  open goalCursor;
  goal_loop: loop
    fetch goalCursor into dimId,dimName;
    if done = 1
    then
      leave goal_loop;
    end if;
    insert into temp_goal values(dimId,dimName);
  end loop goal_loop;
  close goalCursor;
  set done = 0;

  open mcCursor;
  mc_loop: loop
    fetch mcCursor into dimId,dimName;
    if done = 1
    then
      leave mc_loop;
    end if;
    insert into temp_misusecase values(dimId,dimName);
  end loop mc_loop;
  close mcCursor;
  set done = 0;

  open obsCursor;
  obs_loop: loop
    fetch obsCursor into dimId,dimName;
    if done = 1
    then
      leave obs_loop;
    end if;
    insert into temp_obstacle values(dimId,dimName);
  end loop obs_loop;
  close obsCursor;
  set done = 0;

  open personaCursor;
  persona_loop: loop
    fetch personaCursor into dimId,dimName;
    if done = 1
    then
      leave persona_loop;
    end if;
    insert into temp_persona values(dimId,dimName);
  end loop persona_loop;
  close personaCursor;
  set done = 0;

  open reqCursor;
  req_loop: loop
    fetch reqCursor into dimId,dimName;
    if done = 1
    then
      leave req_loop;
    end if;
    insert into temp_requirement values(dimId,dimName);
  end loop req_loop;
  close reqCursor;
  set done = 0;

  open respCursor;
  resp_loop: loop
    fetch respCursor into dimId,dimName;
    if done = 1
    then
      leave resp_loop;
    end if;
    insert into temp_response values(dimId,dimName);
  end loop resp_loop;
  close respCursor;
  set done = 0;

  open riskCursor;
  risk_loop: loop
    fetch riskCursor into dimId,dimName;
    if done = 1
    then
      leave risk_loop;
    end if;
    insert into temp_risk values(dimId,dimName);
  end loop risk_loop;
  close riskCursor;
  set done = 0;

  open roleCursor;
  role_loop: loop
    fetch roleCursor into dimId,dimName;
    if done = 1
    then
      leave role_loop;
    end if;
    insert into temp_role values(dimId,dimName);
  end loop role_loop;
  close roleCursor;
  set done = 0;

  open taskCursor;
  task_loop: loop
    fetch taskCursor into dimId,dimName;
    if done = 1
    then
      leave task_loop;
    end if;
    insert into temp_task values(dimId,dimName);
  end loop task_loop;
  close taskCursor;
  set done = 0;

  open thrCursor;
  thr_loop: loop
    fetch thrCursor into dimId,dimName;
    if done = 1
    then
      leave thr_loop;
    end if;
    insert into temp_threat values(dimId,dimName);
  end loop thr_loop;
  close thrCursor;
  set done = 0;

  open ucCursor;
  uc_loop: loop
    fetch ucCursor into dimId,dimName;
    if done = 1
    then
      leave uc_loop;
    end if;
    insert into temp_usecase values(dimId,dimName);
  end loop uc_loop;
  close ucCursor;
  set done = 0;

  open vulCursor;
  vul_loop: loop
    fetch vulCursor into dimId,dimName;
    if done = 1
    then
      leave vul_loop;
    end if;
    insert into temp_vulnerability values(dimId,dimName);
  end loop vul_loop;
  close vulCursor;
end
