create procedure dataflowsToXml(in includeHeader int)
begin
  declare buf LONGTEXT default '<?xml version="1.0"?>\n<!DOCTYPE dataflows PUBLIC "-//CAIRIS//DTD DATAFLOW 1.0//EN" "http://cairis.org/dtd/dataflow.dtd">\n\n<dataflows>\n';
  declare done int default 0;
  declare tagName varchar(255);
  declare dfName varchar(255);
  declare dfType varchar(255);
  declare envName varchar (200);
  declare fromName varchar (200);
  declare fromType varchar (9);
  declare toName varchar (200);
  declare toType varchar (9);
  declare assetName varchar (200);
  declare obsName varchar (200);
  declare kwd varchar(255);
  declare dfoContext varchar(4000);
  declare compName varchar (200);
  declare compType varchar (20);
  declare tbName varchar(50);
  declare tbDesc varchar(4000);
  declare dfId int;
  declare tbId int;
  declare tbType varchar(255);
  declare envId int;
  declare dfCount int default 0;
  declare tbCount int default 0;
  declare dfCursor cursor for select dataflow, dataflow_type, environment, from_name, from_type, to_name, to_type from dataflows;
  declare dfTagCursor cursor for select t.name from dataflow_tag dt, tag t where dt.dataflow_id = dfId and dt.tag_id = t.id;
  declare tbTagCursor cursor for select t.name from trust_boundary_tag tt, tag t where tt.trust_boundary_id = tbId and tt.tag_id = t.id;
  declare tbCursor cursor for select tb.id,tb.name,tbt.name,tb.description from trust_boundary tb, trust_boundary_type tbt where tb.trust_boundary_type_id = tbt.id;
  declare tbEnvCursor cursor for select distinct environment_id from environment_trust_boundary where trust_boundary_id = tbId;
  declare tbCompCursor cursor for
    select uc.name,'process' from trust_boundary_usecase tbu, usecase uc where tbu.usecase_id = uc.id and trust_boundary_id = tbId and environment_id = envId
    union
    select a.name,'datastore' from trust_boundary_asset tba, asset a where tba.asset_id = a.id and trust_boundary_id = tbId and environment_id = envId;
  declare dfAssetCursor cursor for select a.name from asset a, dataflow_asset da where da.dataflow_id = dfId and da.asset_id = a.id;
  declare dfObsCursor cursor for select o.name,sk.name,do.context from obstacle o, dataflow_obstacle do,stpa_keyword sk where do.dataflow_id = dfId and do.obstacle_id = o.id and do.stpa_keyword_id = sk.id order by 1;
  declare continue handler for not found set done = 1;

  if includeHeader = 0
  then
    set buf = '<dataflows>\n';
  end if;

  open dfCursor;
  df_loop: loop
    fetch dfCursor into dfName, dfType, envName, fromName, fromType, toName, toType;
    if done = 1
    then
      leave df_loop;
    end if;

    set buf = concat(buf,'  <dataflow name=\"',dfName,'\" type=\"',dfType,'\" environment=\"',envName,'\" from_name=\"',fromName,'\" from_type=\"',fromType,'\" to_name=\"',toName,'\" to_type=\"',toType,'\">\n');
    select id into envId from environment where name = envName;
    select dataFlowId(dfName,fromType,fromName,toType,toName,envName) into dfId;

    open dfTagCursor;
    dfTag_loop: loop
      fetch dfTagCursor into tagName;
      if done = 1
      then
        leave dfTag_loop;
      end if;
      set buf = concat(buf,'  <tag name=\"',tagName,'\" />\n'); 
    end loop dfTag_loop;
    close dfTagCursor;
    set done = 0;


    open dfAssetCursor;
    dfAsset_loop: loop
      fetch dfAssetCursor into assetName;
      if done = 1
      then 
        leave dfAsset_loop;
      end if;
      set buf = concat(buf,'    <dataflow_asset name=\"',assetName,'\" />\n');
    end loop dfAsset_loop;
    close dfAssetCursor;
    set done = 0;

    open dfObsCursor;
    dfObs_loop: loop
      fetch dfObsCursor into obsName,kwd,dfoContext;
      if done = 1
      then 
        leave dfObs_loop;
      end if;
      set buf = concat(buf,'    <dataflow_obstacle name=\"',obsName,'\" keyword=\"',kwd,'" >\n      <context>',dfoContext,'</context>\n    </dataflow_obstacle>\n');
    end loop dfObs_loop;
    close dfObsCursor;
    set done = 0;

    set buf = concat(buf,'  </dataflow>\n');
    set dfCount = dfCount + 1;
  end loop df_loop;
  close dfCursor;
  set done = 0;

  open tbCursor;
  tb_loop: loop
    fetch tbCursor into tbId, tbName, tbType, tbDesc;
    if done = 1
    then
      leave tb_loop;
    end if;
    set buf = concat(buf,'  <trust_boundary name=\"',tbName,'\" type=\"',tbType,'" >\n    <description>',tbDesc,'</description>\n');

    open tbTagCursor;
    tbTag_loop: loop
      fetch tbTagCursor into tagName;
      if done = 1
      then
        leave tbTag_loop;
      end if;
      set buf = concat(buf,'    <tag name=\"',tagName,'\" />\n'); 
    end loop tbTag_loop;
    close tbTagCursor;

    set done = 0;
    open tbEnvCursor;
    tbEnv_loop: loop
      fetch tbEnvCursor into envId;
      if done = 1
      then 
        leave tbEnv_loop;
      end if;
      select name into envName from environment where id = envId;
      set buf = concat(buf,'    <trust_boundary_environment name=\"',envName,'\">\n');

      open tbCompCursor;
      tbComp_loop: loop
        fetch tbCompCursor into compName, compType;
        if done = 1
        then
          leave tbComp_loop;
        end if;
        set buf = concat(buf,'      <trust_boundary_component name=\"',compName,'\" type=\"',compType,'\" />\n');
      end loop tbComp_loop;
      close tbCompCursor;
      set done = 0;
      set buf = concat(buf,'    </trust_boundary_environment>\n');
    end loop tbEnv_loop;
    close tbEnvCursor;
    set done = 0;
    set buf = concat(buf,'  </trust_boundary>\n');
    set tbCount = tbCount + 1;
  end loop tb_loop;
  close tbCursor;
  set buf = concat(buf,'</dataflows>');
  select buf,dfCount;
end
//

create procedure dataFlowDiagram(in envName text,in filterType text, in filterElement text)
begin
  declare environmentId int;
  declare compositeCount int;
  select id into environmentId from environment where name = envName limit 1;
  select count(*) into compositeCount from composite_environment where composite_environment_id = environmentId;

  if compositeCount <= 0
  then
    if filterType = 'None' and filterElement != ''
    then
      select dataflow, from_name, from_type, to_name, to_type from dataflows where environment = envName and from_name = filterElement
      union
      select dataflow, from_name, from_type, to_name, to_type from dataflows where environment = envName and to_name = filterElement
      union
      select dataflow, from_name, from_type, to_name, to_type from dataflows where environment = envName and dataflow = filterElement;
    elseif filterType = 'None' and filterElement = ''
    then
      select dataflow, from_name, from_type, to_name, to_type from dataflows where environment = envName;
    elseif filterType != 'None' and filterElement = ''
    then
      select dataflow, from_name, from_type, to_name, to_type from dataflows where environment = envName and from_type = filterType
      union
      select dataflow, from_name, from_type, to_name, to_type from dataflows where environment = envName and to_type = filterType;
    else
      select dataflow, from_name, from_type, to_name, to_type from dataflows where environment = envName and from_name = filterElement and from_type = filterType
      union
      select dataflow, from_name, from_type, to_name, to_type from dataflows where environment = envName and to_name = filterElement and to_type = filterType;
    end if;
  else
    if filterType = 'None' and filterElement != ''
    then
      select dataflow, from_name, from_type, to_name, to_type from dataflows where environment in (select e.name from environment e, composite_environment ce, environment cen where cen.name = envName and cen.id = ce.composite_environment_id and ce.environment_id = e.id) and from_name = filterElement
      union
      select dataflow, from_name, from_type, to_name, to_type from dataflows where environment in (select e.name from environment e, composite_environment ce, environment cen where cen.name = envName and cen.id = ce.composite_environment_id and ce.environment_id = e.id) and to_name = filterElement
      union
      select dataflow, from_name, from_type, to_name, to_type from dataflows where environment in (select e.name from environment e, composite_environment ce, environment cen where cen.name = envName and cen.id = ce.composite_environment_id and ce.environment_id = e.id) and dataflow = filterElement;
    elseif filterType = 'None' and filterElement = ''
    then
      select dataflow, from_name, from_type, to_name, to_type from dataflows where environment in (select e.name from environment e, composite_environment ce, environment cen where cen.name = envName and cen.id = ce.composite_environment_id and ce.environment_id = e.id);
    elseif filterType != 'None' and filterElement = ''
    then
      select dataflow, from_name, from_type, to_name, to_type from dataflows where environment in (select e.name from environment e, composite_environment ce, environment cen where cen.name = envName and cen.id = ce.composite_environment_id and ce.environment_id = e.id) and from_type = filterType
      union
      select dataflow, from_name, from_type, to_name, to_type from dataflows where environment in (select e.name from environment e, composite_environment ce, environment cen where cen.name = envName and cen.id = ce.composite_environment_id and ce.environment_id = e.id) and to_type = filterType;
    else
      select dataflow, from_name, from_type, to_name, to_type from dataflows where environment in (select e.name from environment e, composite_environment ce, environment cen where cen.name = envName and cen.id = ce.composite_environment_id and ce.environment_id = e.id) and from_name = filterElement and from_type = filterType
      union
      select dataflow, from_name, from_type, to_name, to_type from dataflows where environment in (select e.name from environment e, composite_environment ce, environment cen where cen.name = envName and cen.id = ce.composite_environment_id and ce.environment_id = e.id) and to_name = filterElement and to_type = filterType;
    end if;
  end if;
end
