create procedure modelValidation(in envName text)
begin
  declare environmentId int;

  select id into environmentId from environment where name = envName limit 1;

  drop table if exists temp_vout;
  create temporary table temp_vout (label varchar(200), message varchar(1000));

  call integrityAggregationAssociationCheck(environmentId);
  call fairDataProcessing(environmentId);
  call lawfulDataHandling_task(environmentId); 
  call lawfulDataHandling_usecase(environmentId); 
  call necessaryProcessing_usecase(environmentId); 
  call purposeLimitation_usecase(environmentId); 
  call accuracyCheck(environmentId);
  call dataMinimisation_usecase(environmentId);
  call storageLimitationCheck(environmentId);
  call criticalPrivacyRisks(environmentId);
  call newRiskContexts();
  call unrelatedExceptionCheck(environmentId);
  call implicitAssetInclusionCheck(environmentId);
  call implicitAssetVulnerabilityCheck(environmentId);
  call obstructedTasks(environmentId);
  call obstructedGoalDependencies(environmentId);
  call deniedUserGoalDependencies(environmentId);
  call inheritanceInconsistency(environmentId);
  call userGoalLoopCheck();
  call conflictingControl(environmentId);
  call reservedCharacterCheck();
  call accessControlCheck(environmentId);

  select distinct label,message from temp_vout;

end
