create procedure integrityAggregationAssociationCheck(in environmentId int)
begin
  declare headAsset varchar(100);
  declare headAssetType varchar(50);
  declare headPropertyValue int;
  declare headAssocType varchar(50);
  declare headNry varchar(50);
  declare tailNry varchar(50);
  declare tailAssocType varchar(50);
  declare tailPropertyValue int;
  declare tailAssetType varchar(50);
  declare tailAsset varchar(100);
  declare done int default 0;
  declare assocCursor cursor for select ha.name,haty.name,hap.property_value_id,hat.name,hm.name,tm.name,tat.name,tap.property_value_id,taty.name,ta.name from classassociation a, asset ha, multiplicity_type hm, association_type hat, association_type tat, multiplicity_type tm, asset ta, asset_type haty, asset_type taty, asset_property hap, asset_property tap where a.environment_id = environmentId and a.head_id = ha.id and a.head_multiplicity_id = hm.id and a.head_association_type_id = hat.id and a.tail_association_type_id = tat.id and a.tail_multiplicity_id = tm.id and a.tail_id = ta.id and ha.asset_type_id = haty.id and ta.asset_type_id = taty.id and ha.id = hap.asset_id and a.environment_id = hap.environment_id and hap.property_id = 1 and ta.id = tap.asset_id and a.environment_id = tap.environment_id and tap.property_id = 1; 
  declare continue handler for not found set done = 1;

  open assocCursor;
  assoc_loop: loop
    fetch assocCursor into headAsset,headAssetType,headPropertyValue,headAssocType,headNry,tailNry,tailAssocType,tailPropertyValue,tailAssetType,tailAsset;
    if done = 1
    then
      leave assoc_loop;
    end if;

    if ((headAssocType = 'Aggregation' or headAssocType = 'Composition') and (tailAssocType = 'Association') and (headAssetType = 'System' or headAssetType = 'Software' or headAssetType = 'Hardware') and (tailAssetType = 'System' or tailAssetType = 'Software' or tailAssetType = 'Hardware') and (headPropertyValue < tailPropertyValue) )
    then
      insert into temp_vout(label,message) values('Composition/Aggregation Integrity Check',concat('Integrity of ',headAsset,' is lower than ',tailAsset,'.'));
    end if;

    if ((tailAssocType = 'Aggregation' or tailAssocType = 'Composition') and (headAssocType = 'Association') and (tailAssetType = 'System' or tailAssetType = 'Software' or tailAssetType = 'Hardware') and (headAssetType = 'System' or headAssetType = 'Software' or headAssetType = 'Hardware') and (tailPropertyValue < headPropertyValue) )
    then
      insert into temp_vout(label,message) values('Composition/Aggregation Integrity Check',concat('Integrity of ',tailAsset,' is lower than ',headAsset,'.'));
    end if;

  end loop assoc_loop;
  close assocCursor;
end
//

create procedure lawfulDataHandling_task(in environmentId int)
begin
  declare taskId int;
  declare taskName varchar(200);
  declare assetId int;
  declare prCount int;
  declare done int default 0;
  declare taskCursor cursor for select t.name,t.id from environment_task et, task t where et.environment_id = environmentId and et.task_id = t.id;
  declare provisionedAssetCursor cursor for select ta.asset_id from task_asset ta, personal_information pi where ta.task_id = taskId and ta.environment_id = environmentId and ta.asset_id = pi.asset_id and ta.environment_id = pi.environment_id union select ca.source_id from task_concernassociation ca, personal_information pi where ca.task_id = taskId and ca.environment_id = environmentId and ca.source_id = pi.asset_id and ca.environment_id = pi.environment_id union select ca.target_id from task_concernassociation ca, personal_information pi where ca.task_id = taskId and ca.environment_id = environmentId and ca.target_id = pi.asset_id and ca.environment_id = pi.environment_id;
  declare roleCursor cursor for select pr.role_id from persona_role pr, task_persona tp where tp.task_id = taskId and tp.environment_id = environmentId and tp.persona_id = pr.persona_id and tp.environment_id = pr.environment_id;
  declare continue handler for not found set done = 1;

  open taskCursor;
  task_loop: loop
    fetch taskCursor into taskName,taskId;
    if done = 1
    then
      leave task_loop;
    end if;


    open provisionedAssetCursor;
    pa_loop: loop
      fetch provisionedAssetCursor into assetId;
      if done = 1
      then
        leave pa_loop;
      end if;

      select count(pr.role_id) into prCount from persona_role pr, task_persona tp, role r, role_type rt where tp.task_id = taskId and tp.environment_id = environmentId and tp.persona_id = pr.persona_id and tp.environment_id = pr.environment_id and pr.role_id = r.id and r.role_type_id = rt.id and rt.name in ('Data Processor','Data Controller','Data Subject');
      if prCount = 0
      then
        insert into temp_vout(label,message) values('Lawfulness, Fairness, and Privacy: Lawful data handling',concat('Task ',taskName,' handles personal data but no personas associated with this task are data controllers or data processors'));
      end if;
    end loop pa_loop; 
    close provisionedAssetCursor;
    set done = 0;
  end loop task_loop;
  close taskCursor;
  set done = 0;
end
