create procedure lawfulDataHandling_usecase(in environmentId int)
begin
  declare ucId int;
  declare ucName varchar(200);
  declare assetId int;
  declare assetName varchar(200);
  declare arCount int;
  declare done int default 0;
  declare ucCursor cursor for select uc.name,uc.id from environment_usecase eu, usecase uc where eu.environment_id = environmentId and eu.usecase_id = uc.id;
  declare provisionedAssetCursor cursor for select pa.asset_id,a.name from asset a,process_asset pa, personal_information pi where pa.usecase_id = ucId and pa.asset_id = a.id and pa.environment_id = environmentId and pa.asset_id = pi.asset_id and pa.environment_id = pi.environment_id;
  declare continue handler for not found set done = 1;

  open ucCursor;
  uc_loop: loop
    fetch ucCursor into ucName,ucId;
    if done = 1
    then
      leave uc_loop;
    end if;

    open provisionedAssetCursor;
    pa_loop: loop
      fetch provisionedAssetCursor into assetId,assetName;
      if done = 1
      then
        leave pa_loop;
      end if;

      select count(ur.role_id) into arCount from usecase uc, usecase_role ur, process_asset pa, role r, role_type rt where uc.id = ucId and uc.id = ur.usecase_id and uc.id = pa.usecase_id and pa.environment_id = environmentId and ur.role_id = r.id and r.role_type_id = rt.id and rt.name in ('Data Controller','Data Processor','Data Subject');

      if arCount = 0
      then
        insert into temp_vout(label,message) values('Lawfulness, Fairness, and Privacy: Lawful data handling',concat('Usecase ',ucName,' handles personal data (',assetName,') but no actors associated with this use case are data controllers or data processors'));
      end if; 

    end loop pa_loop; 
    close provisionedAssetCursor;
    set done = 0;

  end loop uc_loop;
  close ucCursor;
  set done = 0;

end
