create procedure purposeLimitation_usecase(in environmentId int)
begin
  declare ucId int;
  declare ucName varchar(200);
  declare assetId int;
  declare assetName varchar(200);
  declare msg varchar(1000) default '';
  declare done int default 0;
  declare srgCount int default 0;
  declare ucCursor cursor for select uc.name,uc.id from environment_usecase eu, usecase uc where eu.environment_id = environmentId and eu.usecase_id = uc.id;
  declare provisionedAssetCursor cursor for select pa.asset_id,a.name from asset a,process_asset pa, personal_information pi where pa.usecase_id = ucId and pa.asset_id = a.id and pa.environment_id = environmentId and pa.asset_id = pi.asset_id and pa.environment_id = pi.environment_id;

 
  declare continue handler for not found set done = 1;


  open ucCursor;
  uc_loop: loop
    fetch ucCursor into ucName,ucId;
    if done = 1
    then
      leave uc_loop;
    end if;

    open provisionedAssetCursor;
    pa_loop: loop
      fetch provisionedAssetCursor into assetId,assetName;
      if done = 1
      then
        leave pa_loop;
      end if;

      set msg = concat('Usecase ',ucName,' handles personal data (',assetName,') but no requirements or goals associated with this usecase processes this data.');
      select count(ru.requirement_id) into srgCount from requirement_usecase ru, asset_requirement ar where ru.usecase_id = ucId and ru.requirement_id = ar.requirement_id and ar.asset_id = assetId;
      if srgCount = 0
      then
        select count(ga.goal_id) into srgCount from goalusecase_goalassociation ga, goal_concern gc where ga.environment_id = environmentId and ga.subgoal_id = ucId and ga.goal_id = gc.goal_id and ga.environment_id = gc.environment_id and gc.asset_id = assetId;
      end if;

      if srgCount = 0
      then
        insert into temp_vout(label,message) values('Purpose limitation: Data purpose',msg);
      end if;

    end loop pa_loop; 
    close provisionedAssetCursor;
    set done = 0;

  end loop uc_loop;
  close ucCursor;
  set done = 0;

end
//

create procedure necessaryProcessing_usecase(in environmentId int)
begin
  declare ucId int;
  declare ucName varchar(200);
  declare assetId int;
  declare assetName varchar(200);
  declare msg varchar(1000) default '';
  declare done int default 0;
  declare srgCount int default 0;
  declare ucCursor cursor for select distinct uc.name,uc.id from environment_usecase eu, usecase uc, process_asset pa, personal_information pi where eu.environment_id = environmentId and eu.usecase_id = uc.id and eu.usecase_id = pa.usecase_id and eu.environment_id = pa.environment_id and pa.environment_id = pi.environment_id and pa.asset_id = pi.asset_id;
  declare continue handler for not found set done = 1;


  open ucCursor;
  uc_loop: loop
    fetch ucCursor into ucName,ucId;
    if done = 1
    then
      leave uc_loop;
    end if;

    set msg = concat('Usecase ',ucName,' handles personal data, but no requirements or goals are associated with this usecase.');
    select count(ru.requirement_id) into srgCount from requirement_usecase ru where ru.usecase_id = ucId;
    if srgCount = 0
    then
      select count(ga.goal_id) into srgCount from goalusecase_goalassociation ga where ga.environment_id = environmentId and ga.subgoal_id = ucId;
    end if;

    if srgCount = 0
    then
      insert into temp_vout(label,message) values('Lawfulness, Fairness, and Privacy: Necessary processing',msg);
    end if;

  end loop uc_loop;
  close ucCursor;
  set done = 0;
end
