create procedure lawfulProcessingTable(in envName text)
begin
  declare environmentId int;
  declare ucId int;
  declare ucName varchar(200);
  declare assetId int;
  declare assetName varchar(200);
  declare goalId int;
  declare goalName varchar(50) default '';
  declare goalDef varchar(4000) default '';
  declare goalFC varchar(1000) default '';
  declare goalIssue varchar(1000) default '';
  declare done int default 0;
  declare noGoals int default 1;
  declare paCursor cursor for select ppi.usecase_id,u.name,ppi.asset_id,a.name from process_personal_information ppi, usecase u, asset a where ppi.environment_id = environmentId and ppi.asset_id = a.id and ppi.usecase_id = u.id;
  declare gaCursor cursor for select goal_id from goalusecase_goalassociation where environment_id = environmentId and subgoal_id = ucId;
  declare continue handler for not found set done = 1;

  select id into environmentId from environment where name = envName;

  drop table if exists temp_lp;
  create temporary table temp_lp (usecase varchar(200), asset varchar(200), goal varchar(50), definition varchar(4000), fitcriterion varchar(1000), issue varchar(1000) );

  open paCursor;
  pa_loop: loop
    fetch paCursor into ucId, ucName, assetId, assetName;
    if done = 1
    then
      leave pa_loop;
    end if;

    set noGoals = 1;
    open gaCursor;
    ga_loop: loop
      fetch gaCursor into goalId;
      if done = 1
      then
        if noGoals = 1
        then
          insert into temp_lp (usecase,asset,goal,definition,fitcriterion,issue) values(ucName,assetName,'None','None','None','None');
        end if;
        set noGoals = 0;
        leave ga_loop;
      end if;
      select name into goalName from goal where id = goalId;
      select definition into goalDef from goal_definition where goal_id = goalId and environment_id = environmentId;
      select fitcriterion into goalFC from goal_fitcriterion where goal_id = goalId and environment_id = environmentId;
      select issue into goalIssue from goal_issue where goal_id = goalId and environment_id = environmentId;
      insert into temp_lp (usecase,asset,goal,definition,fitcriterion,issue) values(ucName,assetName,goalName,goalDef,goalFC,goalIssue);
    end loop ga_loop;
    close gaCursor;
    set done = 0;
  end loop pa_loop;
  close paCursor;
  set done = 0;
  select usecase, asset, goal, definition, fitcriterion, issue from temp_lp;
end
//

create procedure synopsesToXml(in includeHeader int)
begin
  declare buf LONGTEXT default '<?xml version="1.0"?>\n<!DOCTYPE synopses PUBLIC "-//CAIRIS//DTD SYNOPSES 1.0//EN" "http://cairis.org/dtd/synopses.dtd">\n\n<synopses>\n';
  declare csCount int default 0;
  declare rsCount int default 0;
  declare ssCount int default 0;
  declare rcCount int default 0;
  declare ucCount int default 0;
  declare tcCount int default 0;
  declare charName varchar(2000);
  declare refName varchar(200);
  declare synName varchar(1000);
  declare synDim varchar(50);
  declare aType varchar(50);
  declare aName varchar(50);
  declare envId int;
  declare envName varchar(100);
  declare ucName varchar(200);
  declare stepNo int;
  declare srcName varchar(2000);
  declare destName varchar(2000);
  declare meName varchar(100);
  declare contName varchar(100);
  declare taskName varchar(200);
  declare gSat varchar(100);
  declare goalName varchar(100);
  declare done int default 0;

  declare csCursor cursor for 
    select pc.description,pcs.synopsis,td.name,'persona',p.name,gs.name from persona_characteristic_synopsis pcs, persona_characteristic pc, trace_dimension td, persona p, goal_satisfaction gs where pcs.characteristic_id = pc.id and pc.persona_id = p.id and pcs.dimension_id = td.id and pcs.satisfaction = gs.id;
  declare rsCursor cursor for
    select dr.name,drs.synopsis,td.name,'persona',p.name,gs.name from document_reference_synopsis drs,document_reference dr, trace_dimension td, persona p, goal_satisfaction gs where drs.reference_id = dr.id and drs.dimension_id = td.id and drs.actor_id = p.id and drs.satisfaction = gs.id;

  declare envCursor cursor for 
    select id,name from environment;

  declare ssCursor cursor for
    select uc.name,ucss.step_no,ucss.synopsis,'asset',a.name from usecase uc, usecase_step_synopsis ucss, asset a where ucss.usecase_id = uc.id and ucss.environment_id = envId and ucss.actor_id = a.id and ucss.actor_type_id = 3
    union
    select uc.name,ucss.step_no,ucss.synopsis,'component',c.name from usecase uc, usecase_step_synopsis ucss, component c where ucss.usecase_id = uc.id and ucss.environment_id = envId and ucss.actor_id = c.id and ucss.actor_type_id = 21
    union
    select uc.name,ucss.step_no,ucss.synopsis,'role',r.name from usecase uc, usecase_step_synopsis ucss, role r where ucss.usecase_id = uc.id and ucss.environment_id = envId and ucss.actor_id = r.id and ucss.actor_type_id = 10;

  declare rcCursor cursor for
    select drs.synopsis, pcs.synopsis, ce.name, lc.name from document_reference_contribution drc, document_reference_synopsis drs, persona_characteristic_synopsis pcs, contribution_end ce, link_contribution lc where drc.reference_id = drs.id and drc.characteristic_id = pcs.characteristic_id and drc.end_id = ce.id and drc.contribution_id = lc.id
    union
    select pcs.synopsis, drs.synopsis, ce.name, lc.name from document_reference_contribution drc, document_reference_synopsis drs, persona_characteristic_synopsis pcs, contribution_end ce, link_contribution lc where drc.characteristic_id = drs.id and drc.reference_id = pcs.characteristic_id and drc.end_id = ce.id and drc.contribution_id = lc.id
    union
    select pcss.synopsis, pcst.synopsis, ce.name, lc.name from document_reference_contribution drc, persona_characteristic_synopsis pcss, persona_characteristic_synopsis pcst, contribution_end ce, link_contribution lc where drc.reference_id = pcss.characteristic_id and drc.characteristic_id = pcst.characteristic_id and drc.end_id = ce.id and drc.contribution_id = lc.id
    union
    select drss.synopsis, drst.synopsis, ce.name, lc.name from document_reference_contribution drc, document_reference_synopsis drss, document_reference_synopsis drst, contribution_end ce, link_contribution lc where drc.reference_id = drss.id and drc.characteristic_id = drst.id and drc.end_id = ce.id and drc.contribution_id = lc.id
    union
    select drs.synopsis, uc.name, ce.name, lc.name from usecase_dr_contribution udc, document_reference_synopsis drs, usecase uc, contribution_end ce, link_contribution lc where udc.usecase_id = uc.id and udc.reference_id = drs.id and udc.end_id = lc.id and udc.contribution_id = ce.id;
  declare ucCursor cursor for
    select uc.name, pcs.synopsis, ce.name, lc.name from usecase_pc_contribution upc, usecase uc, persona_characteristic_synopsis pcs, contribution_end ce, link_contribution lc where upc.usecase_id = uc.id and upc.characteristic_id = pcs.characteristic_id and upc.end_id = ce.id and upc.contribution_id = lc.id;
  declare tcCursor cursor for
    select t.name, e.name, drs.synopsis, lc.name from task t, environment e, document_reference_synopsis drs, link_contribution lc, task_goal_contribution tgc, environment_task et where tgc.task_id = t.id and tgc.task_id = et.task_id and et.environment_id = e.id and tgc.reference_id = drs.id and tgc.contribution_id = lc.id
    union
    select t.name, e.name, pcs.synopsis, lc.name from task t, environment e, persona_characteristic_synopsis pcs, link_contribution lc, task_goal_contribution tgc, environment_task et where tgc.task_id = t.id and tgc.task_id = et.task_id and et.environment_id = e.id and tgc.reference_id = pcs.characteristic_id and tgc.contribution_id = lc.id;

  declare goalCursor cursor for select g.name from user_system_goal_link usgl, goal g, synopsis s where usgl.user_goal_id = s.id and s.synopsis = synName and usgl.system_goal_id = g.id order by 1;


  declare continue handler for not found set done = 1;

  if includeHeader = 0
  then
    set buf = '<synopses>\n';
  end if;

  open csCursor;
  cs_loop: loop
    fetch csCursor into charName,synName,synDim,aType,aName,gSat;
    if done = 1
    then
      leave cs_loop;
    end if;
    set buf = concat(buf,'  <characteristic_synopsis characteristic="',charName,'" synopsis="',synName,'" dimension="',synDim,'" actor_type="',aType,'" actor="',aName,'" satisfaction="',gSat,'">\n');
    open goalCursor;
    goal_loop: loop
      fetch goalCursor into goalName;
      if done = 1
      then
        leave goal_loop;
      end if;
      set buf = concat(buf,'    <system_goal name="',goalName,'"/>\n');
    end loop goal_loop;
    close goalCursor;
    set done = 0;
    set buf = concat(buf,'  </characteristic_synopsis>\n');
    set csCount = csCount + 1;
  end loop cs_loop;
  close csCursor;

  set done = 0;
  open rsCursor;
  rs_loop: loop
    fetch rsCursor into refName,synName,synDim,aType,aName,gSat;
    if done = 1
    then
      leave rs_loop;
    end if;
    set buf = concat(buf,'  <reference_synopsis reference="',refName,'" synopsis="',synName,'" dimension="',synDim,'" actor_type="',aType,'" actor="',aName,'" satisfaction="',gSat,'">\n');
    open goalCursor;
    goal_loop: loop
      fetch goalCursor into goalName;
      if done = 1
      then
        leave goal_loop;
      end if;
      set buf = concat(buf,'    <system_goal name="',goalName,'"/>\n');
    end loop goal_loop;
    close goalCursor;
    set done = 0;
    set buf = concat(buf,'  </reference_synopsis>\n');
    set rsCount = rsCount + 1;
  end loop rs_loop;
  close rsCursor;


  set done = 0;
  open envCursor;
  env_loop: loop
    fetch envCursor into envId,envName;
    if done = 1
    then
      leave env_loop;
    end if;

    open ssCursor;
    ss_loop: loop
      fetch ssCursor into ucName,stepNo,synName,aType,aName;
      if done = 1
      then
        leave ss_loop;
      end if;
      set buf = concat(buf,'  <step_synopsis environment="',envName,'" usecase="',ucName,'" step_no="',stepNo,'" synopsis="',synName,'" actor_type="',aType,'" actor="',aName,'" />\n');
      set ssCount = ssCount + 1;
    end loop ss_loop;
    close ssCursor;
    set done = 0;

  end loop env_loop;
  close envCursor;
  set done = 0;

  set done = 0;
  open rcCursor;
  rc_loop: loop
    fetch rcCursor into srcName,destName,meName,contName;
    if done = 1
    then
      leave rc_loop;
    end if;
      set buf = concat(buf,'  <reference_contribution source="',srcName,'" destination="',destName,'" means_end="',meName,'" contribution="',contName,'" />\n');
      set rcCount = rcCount + 1;
  end loop rc_loop;
  close rcCursor;

  set done = 0;
  open ucCursor;
  uc_loop: loop
    fetch ucCursor into ucName,refName,meName,contName;
    if done = 1
    then
      leave uc_loop;
    end if;
    set buf = concat(buf,'  <usecase_contribution usecase="',ucName,'" referent="',refName,'" means_end="',meName,'" contribution="',contName,'" />\n');
    set ucCount = ucCount + 1;

  end loop uc_loop;
  close ucCursor;

  set done = 0;
  open tcCursor;
  tc_loop: loop
    fetch tcCursor into taskName, envName, synName, contName;
    if done = 1
    then
      leave tc_loop;
    end if;
    set buf = concat(buf,'  <task_contribution task="',taskName,'" environment="',envName,'" referent="',synName,'" contribution="',contName,'" />\n');
    set tcCount = tcCount + 1;

  end loop tc_loop;
  close tcCursor;


  set buf = concat(buf,'</synopses>');
  select buf,csCount,rsCount,ssCount,rcCount,ucCount,tcCount;
  
end
//

create procedure newRiskContexts()
begin
  declare riskId int;
  declare riskName varchar(200);
  declare envName varchar(100);
  declare done int default 0;
  declare riskCursor cursor for select id,name from risk; 
  declare meCursor cursor for select e.name from environment_risk er, environment e where er.id = riskId and er.environment_id = e.id and er.environment_id not in (select em.environment_id from environment_misusecase em, misusecase_risk mr where mr.risk_id = riskId and mr.misusecase_id = em.misusecase_id);
  declare continue handler for not found set done = 1;


  set done = 0;
  open riskCursor;
  risk_loop: loop
    fetch riskCursor into riskId,riskName;
    if done = 1
    then
      leave risk_loop;
    end if;

    open meCursor;
    me_loop: loop
      fetch meCursor into envName;
      if done = 1
      then
        leave me_loop;
      end if;
      insert into temp_vout(label,message) values('New risk contexts',concat("Risk '",riskName,"' is present in environment '",envName,"', but no misuse case has been defined for this environment."));
    end loop me_loop;
    close meCursor;
    set done = 0;
  end loop risk_loop;
  close riskCursor;
end
