create procedure unrelatedExceptionCheck(in environmentId int)
begin
  declare done int default 0;
  declare stepNo int;
  declare envName varchar(50);
  declare excName varchar(200);
  declare ucId int;
  declare ucName varchar(200);
  declare ucCursor cursor for select uc.name,uc.id from environment_usecase eu, usecase uc where eu.environment_id = environmentId and eu.usecase_id = uc.id;
  declare stepCursor cursor for select usne.step_no, usne.name from usecase_step_none_exception usne where usne.usecase_id = ucId and usne.environment_id = environmentId order by 1;
  declare continue handler for not found set done = 1;

  select name into envName from environment where id = environmentId limit 1;

  open ucCursor;
  uc_loop: loop
    fetch ucCursor into ucName,ucId;
    if done = 1
    then
      leave uc_loop;
    end if;

    open stepCursor;
    step_loop: loop
      fetch stepCursor into stepNo, excName;
      if done = 1
      then
        leave step_loop;
      end if;    
      insert into temp_vout(label,message) values('Uncovered exception',concat("Use case '",ucName,"' is present in environment '",envName,"', but step ",stepNo," of the flow in this environment has an exception (", excName,") with no related obstacle."));
    end loop step_loop;
    close stepCursor;
    set done = 0;
  end loop uc_loop;
  close ucCursor;
  set done = 0;
end
//

create procedure reassociateRequirement(in reqName text, in domName text)
begin
  declare domId int;
  declare reqId int;

  select requirementNameId(reqName) into reqId;
  delete from asset_requirement where requirement_id = reqId; 
  delete from environment_requirement where requirement_id = reqId; 

  select id into domId from asset where name = domName limit 1;
  if domId is null
  then
    select id into domId from environment where name = domName limit 1;
    insert into environment_requirement(environment_id,requirement_id) values (domId,reqId);
  else
    insert into asset_requirement(asset_id,requirement_id) values (domId,reqId);
  end if;
end
