  close drCursor;


  set done = 0;
  set buf = concat(buf,'"persona_characteristics" : [\n');
  set headElement = 1;
  open pcCursor;
  pc_loop : loop
    fetch pcCursor into pcId, personaName, bvName, modQual, pcDesc;
