      then
        set headElement = 0;
      else
        set buf = concat(buf,"\n  },\n");
      end if;
    end if;
    set buf = concat(buf,'  {"persona" : "',personaName,'", "behavioural_variable" : "',replace(bvName,' ','_'),'", "modal_qualifier" : "',modQual,'" ,"definition" : "',pcDesc,'",\n    "grounds" : [');
    set pcCount = pcCount + 1;
