        end if;
        set buf = concat(buf,'{"persona" : "',personaName,'", "duration" : "',durationLabel(durationValue),'", "frequency" : "',frequencyLabel(frequencyValue),'", "demands" : "',demandsValue,'", "goal_conflict" : "',gcValue,'"}');
      end loop taskPersona_loop;
