create procedure dataflowsToJSON()
begin
  declare buf LONGTEXT default '';
  declare headElement int default 1;
  declare done int default 0;
  declare dfName varchar(255);
  declare dfType varchar(255);
  declare envName varchar (200);
  declare fromName varchar (200);
  declare fromType varchar (9);
  declare toName varchar (200);
  declare toType varchar (9);
  declare assetName varchar (200);
  declare obsName varchar(100);
  declare kwd varchar(255);
  declare dfoContext varchar(4000);
  declare compName varchar (200);
  declare compType varchar (20);
  declare tbName varchar(50);
  declare tbType varchar(255);
  declare tbDesc varchar(4000);
  declare dfId int;
  declare tbId int;
  declare envId int;
  declare dfCount int default 0;
  declare tbCount int default 0;
  declare dfCursor cursor for select dataflow, dataflow_type, environment, from_name, from_type, to_name, to_type from dataflows;
  declare tbCursor cursor for select tb.id,tb.name,tbt.name,tb.description from trust_boundary tb, trust_boundary_type tbt where tb.trust_boundary_type_id = tbt.id;
  declare tbEnvCursor cursor for select distinct environment_id from environment_trust_boundary where trust_boundary_id = tbId;
  declare tbCompCursor cursor for
    select uc.name,'process' from trust_boundary_usecase tbu, usecase uc where tbu.usecase_id = uc.id and trust_boundary_id = tbId and environment_id = envId
    union
    select a.name,'datastore' from trust_boundary_asset tba, asset a where tba.asset_id = a.id and trust_boundary_id = tbId and environment_id = envId;
  declare dfAssetCursor cursor for select a.name from asset a, dataflow_asset da where da.dataflow_id = dfId and da.asset_id = a.id;
  declare dfObsCursor cursor for select o.name,sk.name,do.context from obstacle o, dataflow_obstacle do where do.dataflow_id = dfId and do.obstacle_id = o.id and do.stpa_keyword_id = sk.id;
  declare continue handler for not found set done = 1;

  set headElement = 1;
  set buf = concat(buf,'"dataflows": [\n');

  open dfCursor;
  df_loop: loop
    fetch dfCursor into dfName, dfType, envName, fromName, fromType, toName, toType;
    if done = 1
    then
      set buf = concat(buf,"]\n\n");
      leave df_loop;
    else
      if headElement = 1
      then
        set headElement = 0;
      else
        set buf = concat(buf,",\n");
      end if;
    end if;

    set buf = concat(buf,'  {"name" : "',dfName,'", type : "',dfType,'", "environment" : "',envName,'", "from_name" : "',fromName,'", "from_type" : "',fromType,'", "to_name" : "',toName,'", "to_type" : "',toType,'", "assets" : [');
    select id into envId from environment where name = envName;
    select dataFlowId(dfName,fromType,fromName,toType,toName,envName) into dfId;

    open dfAssetCursor;
    dfAsset_loop: loop
      fetch dfAssetCursor into assetName;
      if done = 1
      then 
        set buf = concat(buf,']}');
        leave dfAsset_loop;
      else
        if headElement = 1
        then
          set headElement = 0;
        else
          set buf = concat(buf,",");
        end if;
      end if;
      set buf = concat(buf,'"',assetName,'"');
    end loop dfAsset_loop;
    close dfAssetCursor;
    set done = 0;

    open dfObsCursor;
    dfObs_loop: loop
      fetch dfObsCursor into obsName,kwd,dfoContext;
      if done = 1
      then 
        set buf = concat(buf,']}');
        leave dfObs_loop;
      else
        if headElement = 1
        then
          set headElement = 0;
        else
          set buf = concat(buf,",");
        end if;
      end if;
      set buf = concat(buf,'("',obsName,'","',kwd,'","',dfoContext,'")');
    end loop dfObs_loop;
    close dfObsCursor;
    set done = 0;

    set dfCount = dfCount + 1;
  end loop df_loop;
  close dfCursor;

  set buf = concat(buf,',"trust_boundaries": [\n');
  set done = 0;
  set headElement = 1;
  open tbCursor;
  tb_loop: loop
    fetch tbCursor into tbId, tbName, tbType, tbDesc;
    if done = 1
    then
      set buf = concat(buf,"]\n\n");
      leave tb_loop;
    else
      if headElement = 1
      then
        set headElement = 0;
      else
        set buf = concat(buf,",\n");
      end if;
    end if;
    set buf = concat(buf,'  {"name" : "',tbName,'", "type" : "', tbType,'", "description" : "',tbDesc,'", "environments" : [\n');
    open tbEnvCursor;
    tbEnv_loop: loop
      fetch tbEnvCursor into envId;
      if done = 1
      then 
        set buf = concat(buf,']}');
        leave tbEnv_loop;
      else
        if headElement = 1
        then
          set headElement = 0;
        else
          set buf = concat(buf,",\n");
        end if;
      end if;
      select name into envName from environment where id = envId;
      set buf = concat(buf,'    {"name" : "',envName,'", "components" : [');

      set done = 0;
      set headElement = 1;
      open tbCompCursor;
      tbComp_loop: loop
        fetch tbCompCursor into compName, compType;
        if done = 1
        then
          set buf = concat(buf,']\n    }');
          set headElement = 0;
          leave tbComp_loop;
        else
          if headElement = 1
          then
            set headElement = 0;
          else
            set buf = concat(buf,",");
          end if;
        end if;
        set buf = concat(buf,'{"name" : "',compName,'", "type" : "',compType,'"}');
      end loop tbComp_loop;
      close tbCompCursor;
      set done = 0;
    end loop tbEnv_loop;
    close tbEnvCursor;
    set done = 0;
    set tbCount = tbCount + 1;
  end loop tb_loop;
  close tbCursor;
  select buf,dfCount;
end
//

create procedure getUserGoals(in constraintId int)
begin
  if constraintId = -1
  then
    select drs.id, dr.name, drs.synopsis, td.name, p.name,'document_reference',gs.name from document_reference_synopsis drs, document_reference dr, persona p, trace_dimension td, goal_satisfaction gs where drs.reference_id = dr.id and drs.dimension_id = td.id and drs.actor_id = p.id and drs.satisfaction = gs.id
    union
    select pcs.characteristic_id,pc.description,pcs.synopsis, td.name, p.name,'persona_characteristic', gs.name from persona_characteristic_synopsis pcs, persona_characteristic pc, persona p, trace_dimension td, goal_satisfaction gs where pcs.characteristic_id = pc.id and pcs.dimension_id = td.id and pc.persona_id = p.id and pcs.satisfaction = gs.id order by 3;
  else
    select drs.id, dr.name, drs.synopsis, td.name, p.name, 'document_reference',gs.name from document_reference_synopsis drs, document_reference dr, persona p, trace_dimension td, goal_satisfaction gs where drs.id = constraintId and drs.reference_id = dr.id and drs.dimension_id = td.id and drs.actor_id = p.id and drs.satisfaction = gs.id
    union
    select pcs.characteristic_id,pc.description,pcs.synopsis, td.name, p.name, 'persona_characteristic',gs.name from persona_characteristic_synopsis pcs, persona_characteristic pc, persona p, trace_dimension td, goal_satisfaction gs where pcs.characteristic_id = constraintId and pcs.characteristic_id = pc.id and pcs.dimension_id = td.id and pc.persona_id = p.id and pcs.satisfaction = gs.id order by 3;
  end if;
end
//

create procedure addUserGoal(in ugId int,in refName text, in synName text, in dimName text, in personaName text, in gSat text)
begin
  declare refId int;
  declare dimId int;
  declare pId int;
  declare gsId int;

  select id into dimId from trace_dimension where name = dimName limit 1;
  select id into pId from persona where name = personaName limit 1;
  select id into gsId from goal_satisfaction where name = gSat limit 1;

  select id into refId from document_reference where name = refName limit 1;
  if refId is null
  then
    select id into refId from persona_characteristic where description = refName and persona_id = pId limit 1;
    insert into persona_characteristic_synopsis(characteristic_id,synopsis,dimension_id,satisfaction) values (refId,synName,dimId,gsId);
  else 
    insert into document_reference_synopsis(id,reference_id,synopsis,dimension_id,actor_id,actor_type_id,satisfaction) values (ugId,refId,synName,dimId,pId,1,gsId);
  end if;
end
//

create procedure updateUserGoal(in ugId int,in refName text, in synName text, in dimName text, in personaName text, in gSat text)
begin
  declare refId int;
  declare dimId int;
  declare pId int;
  declare gsId int;

  select id into dimId from trace_dimension where name = dimName limit 1;
  select id into pId from persona where name = personaName limit 1;
  select id into gsId from goal_satisfaction where name = gSat limit 1;

  select id into refId from document_reference where name = refName limit 1;
  if refId is null
  then
    select id into refId from persona_characteristic where description = refName and persona_id = pId limit 1;
    update persona_characteristic_synopsis set synopsis = synName, dimension_id = dimId, satisfaction = gsId where characteristic_id = refId;
  else
    update document_reference_synopsis set reference_id = refId, synopsis = synName, dimension_id = dimId, actor_id = pId, actor_type_id = 1, satisfaction = gsId where id = ugId;
  end if;
end
//

create procedure getGoalContributions(in envName text, in personaName text, in filterElement text)
begin

  if personaName = '' and filterElement = ''
  then
    select source, source_type, source_dimension, source_persona, target, target_type, target_dimension, target_persona, means_end, contribution from goal_contribution where environment in (envName,'');
  elseif personaName != '' and filterElement = ''
  then
    select source, source_type, source_dimension, source_persona,  target, target_type, target_dimension, target_persona, means_end, contribution from goal_contribution where source_persona = personaName and environment in (envName,'')
    union
    select source, source_type, source_dimension, source_persona, target, target_type, target_dimension, target_persona, means_end, contribution from goal_contribution where target_persona = personaName and environment in (envName,'');
  elseif personaName = '' and filterElement != ''
  then
    select source, source_type, source_dimension, source_persona,  target, target_type, target_dimension, target_persona, means_end, contribution from goal_contribution where source = filterElement and environment in (envName,'')
    union
    select source, source_type, source_dimension, source_persona, target, target_type, target_dimension, target_persona, means_end, contribution from goal_contribution where target = filterElement and environment in (envName,'');
  else
    select source, source_type, source_dimension, source_persona,  target, target_type, target_dimension, target_persona, means_end, contribution from goal_contribution where source = filterElement and source_persona = personaName and environment in (envName,'')
    union
    select source, source_type, source_dimension, source_persona,  target, target_type, target_dimension, target_persona, means_end, contribution from goal_contribution where target = filterElement and target_persona = personaName and environment in (envName,'');
  end if;
end
